    return res.status(401).json({
      message: 'Unauthorized'
    });
  }
  try {
    const decoded = jwt.verify(accessToken, process.env.ACCESS_TOKEN_SECRET);
    req.user = {
      UserID: decoded.UserID,
      Username: decoded.Username
    };
    next();
  } catch (error) {
    return res.status(403).json({
      message: 'Forbidden'
    });
  }
};
module.exports = {
  verifyToken
};
